class XPickerDateComponent extends XPickerDateProperty {
    constructor(renderer, datePipe, lowerCasePipe, cdr, configService, i18n) {
        super();
        this.renderer = renderer;
        this.datePipe = datePipe;
        this.lowerCasePipe = lowerCasePipe;
        this.cdr = cdr;
        this.configService = configService;
        this.i18n = i18n;
        this.titles = [
            'datePicker.monday',
            'datePicker.tuesday',
            'datePicker.wednesday',
            'datePicker.thursday',
            'datePicker.friday',
            'datePicker.saturday',
            'datePicker.sunday'
        ];
        this.now = new Date();
        this.dates = [];
        this.locale = {};
        this._unSubject = new Subject();
    }
    get rangeStart() {
        if (this.rangeValue && this.rangeValue.length > 0) {
            return this.rangeValue[0];
        }
        return '';
    }
    get rangeEnd() {
        if (this.rangeValue && this.rangeValue.length > 1) {
            return this.rangeValue[1];
        }
        return '';
    }
    ngOnInit() {
        this.i18n.localeChange
            .pipe(map((x) => x.datePicker), takeUntil(this._unSubject))
            .subscribe((x) => {
            this.locale = x;
            this.cdr.markForCheck();
        });
    }
    ngOnChanges(simples) {
        const { display, rangeType } = simples;
        XIsChange(display) && this.init();
        if (rangeType) {
            console.log(this.rangeType);
        }
    }
    ngOnDestroy() {
        this._unSubject.next();
        this._unSubject.unsubscribe();
    }
    init() {
        this.setDays(this.display);
    }
    setDays(date) {
        let dates = [];
        const year = date.getFullYear();
        const month = date.getMonth();
        const first = new Date(year, month, 1);
        const last = new Date(year, month + 1, 0);
        const lastDate = last.getDate();
        const firstDay = first.getDay();
        const lastDay = last.getDay();
        let day = firstDay;
        let index = 1;
        while (day !== 1) {
            index--;
            let date = new Date(year, month, index);
            dates = [date, ...dates];
            day = date.getDay();
        }
        index = 1;
        do {
            dates = [...dates, new Date(year, month, index)];
            index++;
        } while (index <= lastDate);
        index = 0;
        day = lastDay;
        while (day !== 0 || dates.length !== 7 * 6) {
            index++;
            let date = new Date(year, month + 1, index);
            dates = [...dates, date];
            day = date.getDay();
        }
        this.dates = chunk(dates, 7);
        if (this.dates.length > 0) {
            this.rangeChange.emit([dates[0], dates[dates.length - 1]]);
        }
        this.cdr.detectChanges();
    }
    dateClick(date) {
        this.model = date;
        this.modelChange.emit(date);
        this.cdr.markForCheck();
    }
    getLocaleMonth(date) {
        return this.locale[this.lowerCasePipe.transform(this.datePipe.transform(date, 'LLLL'))];
    }
    rangeDisabled(date) {
        if (this.rangeType === 'end') {
            return this.rangeStart !== '' && date.getTime() < this.rangeStart;
        }
        else if (this.rangeType === 'start') {
            return this.rangeEnd !== '' && date.getTime() > this.rangeEnd;
        }
        return false;
    }
    setDisplay(date) {
        this.display = new Date(date.getFullYear(), date.getMonth(), 1);
        this.displayChange.emit(this.display);
        this.setDays(this.display);
    }
    nextMonth(num) {
        let date = new Date(this.display);
        date.setMonth(date.getMonth() + num);
        this.setDisplay(date);
        this.monthChange.emit(num);
        this.cdr.detectChanges();
    }
    nextYear(num) {
        let date = new Date(this.display);
        date.setFullYear(date.getFullYear() + num);
        this.setDisplay(date);
        this.yearChange.emit(num);
        this.cdr.detectChanges();
    }
    typeOnChange(type) {
        this.type = type;
        this.typeChange.emit(type);
        this.cdr.detectChanges();
    }
    trackByNode(_index, item) {
        return item;
    }
